mapper.insert:
INSERT /* TemplateMapper.insertEtcManagement */
INTO etc_mng
(
	title,acpt_dt,doc_no,isu_cd,acpt_no,contents,confirm_date,etc,subsidiary,ename,agent,major_biz,major_sub_com_yn,total_assets,ctrl_total_assets,ctrl_total_assets_ratio
)
VALUES
(
	#{title},#{acpt_dt},#{doc_no},#{isu_cd},#{acpt_no},#{contents},#{confirm_date},#{etc},#{subsidiary},#{ename},#{agent},#{major_biz},#{major_sub_com_yn},#{total_assets},#{ctrl_total_assets},#{ctrl_total_assets_ratio}
)
